import React from "react";
import * as SortingAlgorithms from "../SortingAlgorithms/SortingAlgorithms";
import './SortingVisualizer.css';
import { set } from "mongoose";

export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            array: [],
        };
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        const array = [];
        for (let i=0; i < 150; i++)
        {
            array.push(randomIntFromInterval(5,750));
        }
        this.setState({array});
    }

    mergeSort() {
        const animations = SortingAlgorithms.mergeSort(this.state.array);
        for (let i = 0; i < animations.length; i++) {
            const {comparison, swap} = animations[i];
            setTimeout(() => {
                const arrayBars = document.getElementsByClassName('array-bar');
                arrayBars[comparison[0]].style.background = 'linear-gradient(180deg, #6DD5FA, #2980B9)';
                arrayBars[comparison[1]].style.background = 'linear-gradient(180deg, #6DD5FA, #2980B9)';
                setTimeout(() => {
                    // arrayBars[comparison[0]].style.background = 'linear-gradient(180deg, #FF7A89, #C1666B)';
                    // arrayBars[comparison[1]].style.background = 'linear-gradient(180deg, #FF7A89, #C1666B)';
                }, (i+1) * 10);
            }, (i+1) * 10);
        }
    }

    quickSort() {}

    heapSort() {}

    bubbleSort() {}

    render() {
        const {array} = this.state;

        return (
            <div>
                <div className="array-container">
                    {array.map((value, idx) => (
                        <div className="array-bar" key={idx} style={{height:`${value}px`}}>
                            {/* {value} */}
                        </div>
                    ))}
                </div>
                <button class="generate-array" onClick={() => this.resetArray()}>Generate New Array</button>
                <button class="sort-button" onClick={() => this.mergeSort()}>Merge Sort</button>
                <button class="sort-button" onClick={() => this.quickSort()}>Quick Sort</button>
                <button class="sort-button" onClick={() => this.heapSort()}>Heap Sort</button>
                <button class="sort-button" onClick={() => this.bubbleSort()}>Bubble Sort</button>
            </div>
        );
    }
}

function randomIntFromInterval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}

